import React, { useState } from 'react';

const Counter = () => {
const [change, setChange] = useState(0)


const increaseHandler = (e) => {
setChange(change+1)
}

    return (
        <div>
        if(change)
        <p>{change}</p>
            <button onClick={increaseHandler}>Increase</button>
        </div>
    )
}

export default Counter;
