import React, { useState } from 'react';
import Button from './UI/Button';

const AddUser = (props) => {
const [enteredName, setEnteredName] = useState('');
const [enteredAge, setEnteredAge] = useState('');
const [error, setError] = useState();

const nameChangeHandler = (event) => {
    setEnteredName(event.target.value)
};

const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value)
};


const manageForm = (event) => {
event.preventDefault();

if(enteredName.trim() === 0 && enteredAge.trim() === 0) {
    setError({
        title: "invalid user input",
        message: "put valid input"
    });
    return;
}

if(+enteredAge <1){
    setError({
       title: "invalid user age",
        message: "put valid age" 
    });
    return;
}

props.onAddList(enteredName, enteredAge);

setEnteredName('')
setEnteredAge('')
}

const errorhandler = () => {
    setError(null);
}

    
    return (
        <div>
        {error && <Error 
        title={error.title}
        message={error.message}
        onConfirm={errorhandler}
         />}
            <form onSubmit={manageForm}>
            <label htmlFor='name'>name</label>
            <input id='name' value={enteredName} type='text' onChange={nameChangeHandler} />
            <label htmlFor='age'>age</label>
            <input id='age' value={enteredAge} type='text' onChange={ageChangeHandler} />
            <Button type='submit'>Add</Button>
            </form>
        </div>
    )
}

export default AddUser;
